import{_ as a,c as r,j as e,a as s,o}from"./chunks/framework.BGo0cstt.js";const _=JSON.parse('{"title":"클리포 활용 계기 및 목적","description":"","frontmatter":{},"headers":[],"relativePath":"blog/first.md","filePath":"blog/first.md"}'),n={name:"blog/first.md"};function l(i,t,c,d,f,p){return o(),r("div",null,t[0]||(t[0]=[e("h1",{id:"클리포-활용-계기-및-목적",tabindex:"-1"},[s("클리포 활용 계기 및 목적 "),e("a",{class:"header-anchor",href:"#클리포-활용-계기-및-목적","aria-label":'Permalink to "클리포 활용 계기 및 목적"'},"​")],-1),e("p",null,"디지털 전환의 바람과 함께 – 그린스마트 미래학교(현 공간재구조화사업)에서의 첫걸음 2021년 6월, 우리학교는 그린스마트 미래학교(현 공간재구조화사업)로 지정되면서 자연스럽게 수업과 공간에 대한 깊은 고민을 시작하게 되었습니다. 디지털 전환이라는 사회적 요구에 부응하여, 기존의 수업 경험을 새롭게 디자인하자고 교사들은 의기투합하게 되었습니다. 우리는 ‘최초의 학교를 짓는다.’는 마음으로 수업과 평가에 집중하며 학생들에게 더 나은 학습 환경을 제공하고자 노력했습니다. AI와의 첫 만남 - 우왕좌왕 속에서 길을 찾다 그 후, 우리는 수업 설계, 평가, 기록을 어떻게 유기적이고 체계적으로 할 수 있을까를 고민하며 여러 학교와 전문가들을 찾아다녔습니다. 그러던 중 2021년 11월 말, AI 스타트업 ‘데이터드리븐’으로부터 협업 제안을 받았습니다. 처음에는 어디서부터 어떻게 시작해야 할지 몰라 많은 회의를 거듭했지만, 점차 논의가 구체화되며 협업의 방향성이 잡히기 시작했습니다. 수업 중 이루어지는 학생들의 모습을 기록하고, 이 기록들을 모아 학생들이 잘할 수 있는 과목과 영역을 찾아 그들의 삶을 설계하는 데 도움을 주자는 목표가 생겨났습니다. 이는 안용중학교의 3년간의 졸업 앨범에 학생들의 생활 모습과 수업 기록을 엮어보자는 아이디어로 발전되었습니다. 클릭 한 번의 혁신 - 클리포(CLIPO)의 탄생 이야기 이 과정에서 우리는 교사들에게 추가적인 부담을 주지 않도록 하는 것이 매우 중요하다는 것을 깨달았습니다. 좋은 취지가 교사들의 업무량 증가로 이어지면, 오히려 본래의 목적을 잃을 수 있다는 우려가 있었습니다. 그래서 모든 작업이 클릭 한 번으로 이루어질 수 있도록 기획해 보자는 결론에 이르렀습니다. 이렇게 해서 ‘클릭 한 번으로 모든 학생에게 리포트를 제공하자.’라는 의미를 지닌 클리포(CLIPO)가 탄생하게 되었습니다. 선생님과 AI, 손을 잡다 - 클리포와 함께 한 첫 학기 2022년 3월부터 클리포 개발에 박차를 가했고, 같은 해 9월부터는 국어, 수학, 기술·가정, 음악, 한문 5개 교과에서 시범 운영을 시작했습니다. 선생님들은 분석적 루브릭을 활용해 학생들을 평가하고, 이 평가를 바탕으로 AI를 통해 학생들의 기록을 생성하는 과정을 처음 경험했습니다. 이는 번거롭고 수고스러운 일이었지만, ‘한 명도 차별하지 않고, 소홀히 하지 않겠다’ 라는 학교 비전에 맞추어 모든 학생들에게 세심하게 다가갈 수 있는 기회로 받아들였습니다.",-1),e("p",null,"미래로 가는 나침반 - 학생들의 성장을 위한 클리포의 역할 2023년 1월, 학생들과 학부모들은 처음 받아보는 개인별 리포트에 대해 매우 신기해했고, 학교와 교사들이 학생들에게 얼마나 세심한 관심을 기울이고 있는지에 대해 큰 감동을 받았습니다. 이로 인해 2023년 3월부터는 모든 교과에서 2개 이상의 수행평가 또는 프로젝트를 계획하고 실행하여 기록하기로 교사들 간에 합의하게 되었습니다. 물론 이는 많은 노력과 시간이 필요한 일이었지만, 교사로서의 자부심과 성취감을 느낄 수 있는 기회이기도 했습니다. 클릭으로 수업과 평가에 도전하다 - 끝없는 발전을 향한 클리포의 여정 현재 우리는 어떻게 하면 클리포를 더욱 밀도 있게, 손쉽게, 그리고 내실 있게 발전시킬 수 있을지에 대해 계속 고민하고 있습니다. 학교와 데이터드리븐은 지속적으로 협의하며, 클리포의 기능을 개선하고 확장해 나가고 있습니다. 이 과정이 학생들의 삶에 작은 밀알이 되어 튼튼하게 뿌리내리기를 간절히 소망합니다. 업무 협약 클리포 개발 실무진 협의 클리포 활용 전체 교원 연수 클리포 활용 사례 분석",-1)]))}const u=a(n,[["render",l]]);export{_ as __pageData,u as default};
